properties.property:
local Serial = require "actions/Serial"
local MessageBox = require "actions/MessageBox"
local Menu = require "actions/Menu"
local Action = require "actions/Action"
local Do = require "actions/Do"

local Transform = require "util/Transform"
local Layout = require "util/Layout"

return function(self)
    if GameState.leader == "sonic" then
        return Serial {
            MessageBox {message="Sonic: Locked. {p50}I wonder what's in there...", blocking = true, textSpeed=4},
            Do(function()
                self:refreshKeyHint()
            end)
        }
    elseif GameState.leader == "sally" then
        return Serial {
            MessageBox {message="Sally: I haven't read some of these in quite awhile... {p40}! {p20}'The adventures of Windom'! {p20}I remember my father reading this to me.", blocking = true},
            MessageBox {message="Sally: Maybe I could read it to Tails.", blocking = true}
        }
    end
    return Action()
end

