properties.property:
local BlockPlayer = require "actions/BlockPlayer"
local MessageBox = require "actions/MessageBox"
local Menu = require "actions/Menu"
local Action = require "actions/Action"
local Do = require "actions/Do"

local Transform = require "util/Transform"
local Layout = require "util/Layout"

return function(self)
    if GameState.leader == "sonic" then
        return BlockPlayer {
            MessageBox {message="Sonic: Locked. {p50}I wonder what's in there...", textSpeed=4},
            Do(function()
                self:refreshKeyHint()
            end)
        }
    elseif GameState.leader == "sally" then
        return BlockPlayer {
            MessageBox {message="Sally: ...", textspeed=2},
            Do(function()
                self:refreshKeyHint()
            end)
        }
    elseif GameState.leader == "bunny" then
        return BlockPlayer {
            MessageBox {message="Bunnie: My daddy taught me not to snoop.", textspeed=2},
            Do(function()
                self:refreshKeyHint()
            end)
        }
    end
    return Action()
end

local BlockPlayer = require "actions/BlockPlayer"
local MessageBox = require "actions/MessageBox"

return function(self)
    return BlockPlayer {
        MessageBox {message="Nicole: I detect several personal effects within this chest, {p40}Sally."},
        MessageBox {message="Nicole: Family pictures, {p40}royal garb, {p40}family heirlooms..."},
        MessageBox {message="Sally: That's enough Nicole."}
    }
end

local BlockPlayer = require "actions/BlockPlayer"
local MessageBox = require "actions/MessageBox"

return function(self)
    return BlockPlayer {
        MessageBox {message="Nicole: This is your bed, {p40}Sally."},
        MessageBox {message="Nicole: ...{p40}are you tired, or something?"}
    }
end

local BlockPlayer = require "actions/BlockPlayer"
local Do = require "actions/Do"
local MessageBox = require "actions/MessageBox"

return function(self)
    return BlockPlayer {
        MessageBox {message="Nicole: I am detecting a great deal of dust, {p40}Sally."},
        Do(function()
            self.scene.player.noIdle = true
            self.scene.player.state = "thinking"
        end),
        MessageBox {message="Sally: Ha, {p40}ha. {p60}Very funny, Nicole."},
        Do(function()
            self.scene.player.state = "thinking2"
        end),
        MessageBox {message="Sally: I guess I haven't had much time to read recently...", textspeed=2},
        Do(function()
            self.scene.player.noIdle = false
        end)
    }
end

local Serial = require "actions/Serial"
local Parallel = require "actions/Parallel"
local MessageBox = require "actions/MessageBox"
local Menu = require "actions/Menu"
local Action = require "actions/Action"
local Wait = require "actions/Wait"
local BlockPlayer = require "actions/BlockPlayer"
local DescBox = require "actions/DescBox"
local Do = require "actions/Do"
local PlayAudio = require "actions/PlayAudio"
local YieldUntil = require "actions/YieldUntil"

local Transform = require "util/Transform"
local Layout = require "util/Layout"

return function(self)
    self.menuClosing = false
    local descBox = MessageBox{
        message="Computer: What would you like to do?",
        noPressX = true,
        closeAction = YieldUntil(self, "menuClosing")
    }
    return BlockPlayer {
        PlayAudio("sfx", "nicolebeep", 1.0, true),
        Parallel {
            descBox,
            Serial {
                Wait(1),
                Menu {
                    layout = Layout {
                        {Layout.Text("Practice fighting"), choose = function(menu)
                            local fightMenuOpts = {{Layout.Text("Which boss?"), selectable = false}}
                            local bots = {"rover"}
                            for _, name in pairs(bots) do
                                local bot = require ("data/monsters/"..name)
                                table.insert(fightMenuOpts, {Layout.Text(bot.name), choose = function(menu2)
                                    self.scene:run {
                                        Menu {
                                            layout = Layout {
                                                {Layout.Text("Fight "..bot.name.."?"), selectable = false},
                                                {Layout.Text("Yes"), choose = function(menu3)
                                                    menu:close()
                                                    menu2:close()
                                                    menu3:close()
                                                    self.menuClosing = true
                                                    self.scene:run {
                                                        menu3,
                                                        menu2,
                                                        menu,
                                                        descBox,
                                                        self.scene:enterBattle {
                                                            opponents = {name},
                                                            music = "boss",
                                                            bossBattle = true,
                                                            practice = true
                                                        },
                                                        MessageBox{message="Computer: Fighting simulation complete.", blocking = true},
                                                        -- Hack to fix bug returning from battle
                                                        Do(function()
                                                            self.scene.player.cinematicStack = 1
                                                        end)
                                                    }
                                                end},
                                                {Layout.Text("No"), choose = function(menu3)
                                                    menu3:close()
                                                end}
                                            },
                                            cancellable = true,
                                            transform = Transform(love.graphics.getWidth()/2, love.graphics.getHeight()/2+30),
                                            selectedRow = 2
                                        }
                                    }
                                end})
                            end
                            self.scene:run {
                                Parallel {menu,
                                Menu {
                                    layout = Layout(fightMenuOpts),
                                    cancellable = true,
                                    transform = Transform(love.graphics.getWidth()/2 + 200, love.graphics.getHeight()/2 + 30),
                                    selectedRow = 2
                                }}
                            }
                        end, desc = "Practice fighting against past bosses."},
                        {Layout.Text("Practice sneaking"), choose = function(menu)
                            self.scene:run {
                                Parallel {menu,
                                Menu {
                                    layout = Layout {
                                        {Layout.Text("Are you sure?"), selectable = false},
                                        {Layout.Text("Yes"), choose = function(menu2)
                                            menu:close()
                                            menu2:close()
                                            self.menuClosing = true
                                            self.scene:run {
                                                menu2,
                                                menu,
                                                descBox,
                                                Do(function()
                                                    self.scene.audio:stopMusic()
                                                    self.scene:changeScene{map="stealthtut1", tutorial=true}
                                                end),
                                                MessageBox {
                                                    message = "Computer: Stealth simulation complete.",
                                                    blocking = true
                                                },
                                                -- Hack to fix bug returning from battle
                                                Do(function()
                                                    self.scene.player.cinematicStack = 1
                                                end)
                                            }
                                        end},
                                        {Layout.Text("No"), choose = function(menu2)
                                            menu2:close()
                                        end}
                                    },
                                    cancellable = true,
                                    transform = Transform(love.graphics.getWidth()/2, love.graphics.getHeight()/2+30),
                                    selectedRow = 2
                                }}
                            }
                        end, desc = "Fighting isn't the only option."},
                        {Layout.Text("Give me a hint"), choose = function(menu)
                            menu:close()
                            self.menuClosing = true
                            local msg = ""
                            if not GameState:isFlagSet("rotorreveal_done") then
                                msg = "Rotor's locked himself in his workshop for quite awhile, hasn't he?"
                            else
                                msg = "You're still here?? {p60}Shouldn't you be at the Death Egg right now?"
                            end

                            self.scene:run {
                                menu,
                                descBox,
                                MessageBox {
                                    message = "Computer: "..msg,
                                    blocking = true,
                                    textSpeed = 4
                                }
                            }
                        end, desc = "Forgot what you were supposed to be doing?"}
                    },
                    cancellable = true,
                    withClose = Do(function() descBox:close() end),
                    transform = Transform(love.graphics.getWidth()/2, love.graphics.getHeight()/2 + 30)
                }
            }
        },
        Do(function()
            self:refreshKeyHint()
        end)
    }
end
